mapper.select:
SELECT COUNT(*)
FROM customer
WHERE segment_id = #{segmentId}

SELECT
      DISTINCT snapshot_month
 FROM customer_churn_snapshot
WHERE snapshot_month  >=  #{fromMonth}
  AND snapshot_month  <=  #{toMonth}
ORDER BY snapshot_month

SELECT
       COALESCE(ROUND(SUM(A.monthly_payment) / NULLIF(COUNT(DISTINCT A.cum_id), 0), 0), 0)
  FROM contract A
 WHERE A.start_date >= #{from}
   AND A.start_date <  #{to}

SELECT
       COALESCE(ROUND(AVG(A.star), 1), 0)
  FROM feedback A
 WHERE A.create_date >= #{from}
   AND A.create_date <  #{to}

SELECT
       COUNT(DISTINCT A.cum_id)
  FROM contract A
 WHERE A.start_date >= #{from}
   AND A.start_date <  #{to}
