mapper.select:
SELECT po.id AS overdueId, po.pay_overdue_code AS payOverdueCode, c.name AS customerName, c.in_charge AS inCharge, c.call_num AS callNum, con.contract_code AS contractCode, con.monthly_payment AS monthlyPayment, po.due_date AS dueDate, po.overdue_period AS overduePeriod, po.status AS status FROM pay_overdue po JOIN customer c ON c.id = po.cum_id JOIN contract con ON con.id = po.contract_id
AND po.status = #{status}
AND c.name LIKE CONCAT('%', #{criteria.keyword}, '%')
AND con.contract_code LIKE CONCAT('%', #{criteria.keyword}, '%')
AND po.pay_overdue_code LIKE CONCAT('%', #{criteria.keyword}, '%')
ORDER BY po.pay_overdue_code DESC LIMIT #{criteria.amount} OFFSET #{criteria.offset}

SELECT io.id AS overdueId, io.item_overdue_code AS itemOverdueCode, c.name AS customerName, c.in_charge AS inCharge, c.call_num AS callNum, con.contract_code AS contractCode, io.count AS count, io.due_date AS dueDate, io.overdue_period AS overduePeriod, io.status AS status FROM item_overdue io JOIN customer c ON c.id = io.cum_id JOIN contract con ON con.id = io.contract_id
AND io.status = #{status}
AND c.name LIKE CONCAT('%', #{criteria.keyword}, '%')
AND con.contract_code LIKE CONCAT('%', #{criteria.keyword}, '%')
AND io.item_overdue_code LIKE CONCAT('%', #{criteria.keyword}, '%')
ORDER BY io.id DESC LIMIT #{criteria.amount} OFFSET #{criteria.offset}
