mapper.select:
WITH total AS ( SELECT COUNT(*) AS totalCustomerCount FROM customer WHERE is_deleted = 'N' ), risk AS ( SELECT COUNT(*) AS riskCustomerCount FROM customer c JOIN segment s ON s.id = c.segment_id WHERE c.is_deleted = 'N' AND s.name = #{riskSegmentName} ), avg_this AS ( SELECT ROUND(AVG(f.star), 1) AS avgStarThis FROM feedback f JOIN customer c ON c.id = f.cum_id WHERE c.is_deleted = 'N' AND f.create_date BETWEEN #{monthStart} AND #{monthEnd} ), avg_prev AS ( SELECT ROUND(AVG(f.star), 1) AS avgStarPrev FROM feedback f JOIN customer c ON c.id = f.cum_id WHERE c.is_deleted = 'N' AND f.create_date BETWEEN #{prevStart} AND #{prevEnd} )
SELECT /* ① 이탈 위험 고객 비중 */ ROUND((risk.riskCustomerCount * 1.0 / total.totalCustomerCount) * 100, 1) AS riskCustomerRate, risk.riskCustomerCount AS riskCustomerCount, total.totalCustomerCount AS totalCustomerCount,
/* ② 전월 대비 변화(평균 만족도 MoM) */ ROUND((avg_this.avgStarThis - avg_prev.avgStarPrev), 1) AS momChangeRate,
/* ③ 가장 위험한 세그먼트(이번달 세그먼트 평균 최저) */ rs.mostRiskySegmentName, rs.mostRiskySegmentRate,
/* ④ 2.5 이하 세그먼트 개수 / 전체 세그먼트 수 */ COALESCE(dist.lowSatisfactionSegmentCount, 0) AS lowSatisfactionSegmentCount, COALESCE(dist.totalSegmentCount, 0) AS totalSegmentCount
FROM total CROSS JOIN risk CROSS JOIN avg_this CROSS JOIN avg_prev
/* ③ 위험 세그먼트: 평균 평점이 가장 낮은 세그먼트 */ LEFT JOIN ( SELECT s.name AS mostRiskySegmentName, ROUND(AVG(f.star), 1) AS mostRiskySegmentRate FROM segment s JOIN customer c ON c.segment_id = s.id AND c.is_deleted = 'N' JOIN feedback f ON f.cum_id = c.id AND f.create_date BETWEEN #{monthStart} AND #{monthEnd} GROUP BY s.id, s.name ORDER BY mostRiskySegmentRate ASC LIMIT 1 ) rs ON 1=1
/* ④ 분포 KPI: avg<=2.5 세그먼트 수 + 전체 세그먼트 수 */ LEFT JOIN ( SELECT COUNT(*) AS totalSegmentCount, SUM(CASE WHEN avgStar <= 2.5 THEN 1 ELSE 0 END) AS lowSatisfactionSegmentCount FROM ( SELECT s.id, AVG(f.star) AS avgStar FROM segment s JOIN customer c ON c.segment_id = s.id AND c.is_deleted = 'N' JOIN feedback f ON f.cum_id = c.id AND f.create_date BETWEEN #{monthStart} AND #{monthEnd} GROUP BY s.id ) seg_avg ) dist ON 1=1
