mapper.select:
SELECT COUNT(*)
FROM contract c
WHERE c.status IN ('P','I')
AND DATE_ADD(c.start_date, INTERVAL c.contract_period MONTH) >= #{now}
AND DATE_ADD(c.start_date, INTERVAL c.contract_period MONTH) <= #{until}

SELECT COALESCE(SUM(c.total_amount), 0)
FROM contract c
WHERE c.start_date >= #{start}
AND c.start_date <= #{end}
AND c.status IN ('P','I')
